package Operators;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int X=12, Y=13, Z=16;

		System.out.println("The Assignment Value is : ");

		X+=2;// (X=X+2)
		Y-=2; //(Y=Y-2)
		Z*=2; //(Z=Z*2)
		
		System.out.println("The Value of X is : " +X); //14
		System.out.println("The Value of Y is : " +Y); // 11
		System.out.println("The Value of Z is : " +Z); // 32
		
		
		
	}

}
