package Operators;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=5;
		
		System.out.println("Value of a "+a);
		System.out.println("Value of b "+b);
		
		System.out.println("\n");
		
		System.out.println("Value of ++a "+(++a));
		System.out.println("Value of a "+a);
		
		System.out.println("\n");
		
		System.out.println("Value of b++ "+(b++));
		System.out.println("Value of b "+b);
		
		
	}

}
