package Operators;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean a = true;
		boolean b = false;

		System.out.println("a && b = " + (a&&b)); // false
		System.out.println("a || b = " + (a||b) ); // true
		System.out.println("!(a && b) = " + !(a && b)); // true
		
		
	}

}
