package Operators;

public class ShiftOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 34;
		int b = -20;
		
		System.out.println("Signedd Right Shift 34 divided by 2 = " + (a>>1));
		System.out.println("Signedd Right Shift -20 divided by 2 = " + (b>>1));
		System.out.println("Signedd Left Shift 34 divided by 2 = " + (a<<1));
		System.out.println("Signedd Left Shift -20 divided by 2 = " + (b<<1));
		System.out.println("UnSignedd Right Shift 34 = " + (a>>>1));
		System.out.println("UnSignedd Right Shift -20 = " + (b>>>1));

		
		
	}

}
